// export default ChatWindow;
import React, { useState, useEffect } from 'react';
import { fetchConversation, appendToConversation } from '../api/chatApi';

const ChatWindow = ({ chatId }) => {
  const [chat, setChat] = useState(null);
  const [prompt, setPrompt] = useState('');
  const [file, setFile] = useState(null);

  useEffect(() => {
    const loadChat = async () => {
      if (chatId) {
        const data = await fetchConversation(chatId);
        setChat(data);
      }
    };
    loadChat();
  }, [chatId]);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('prompt', prompt);
    if (file) {
      formData.append('file', file);
    }
    const dummyResponse = 'This is a dummy response';
    const updatedChat = await appendToConversation(chatId, prompt, dummyResponse, formData);
    setPrompt('');
    setFile(null);
    setChat(updatedChat);
  };

  const firstPrompt = chat?.history && chat.history[0] ? chat.history[0].prompt : 'Untitled Chat';

  return (
    <div className="flex flex-col h-screen bg-gray-100">
      <header className="p-4 bg-gray-900 text-white">
        <h1 className="text-xl font-semibold">{firstPrompt || 'Chat'}</h1>
      </header>
      <div className="flex-grow overflow-y-auto p-4">
        <ul className="space-y-4">
          {chat?.history.map((entry, index) => (
            <li key={index} className="p-4 bg-white rounded-lg shadow">
              <div className="font-medium text-gray-800">{entry.prompt}</div>
              <div className="mt-1 text-gray-600">{entry.response}</div>
            </li>
          ))}
        </ul>
      </div>
      <footer className="p-4 bg-gray-900">
        <form onSubmit={handleSubmit} className="flex items-center space-x-2">
          <input
            type="text"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            placeholder="Type your prompt"
            className="flex-grow px-4 py-2 rounded-lg bg-gray-800 text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
          <input
            type="file"
            onChange={handleFileChange}
            className="hidden"
            id="file-upload"
          />
          <label
            htmlFor="file-upload"
            className="px-4 py-2 bg-gray-800 text-white rounded-lg hover:bg-gray-700 cursor-pointer"
          >
            Attach
          </label>
          <button
            type="submit"
            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-500"
          >
            Send
          </button>
        </form>
      </footer>
    </div>
  );
};

export default ChatWindow;
