// export default Sidebar;
import React, { useEffect, useState } from 'react';
import { fetchConversations, deleteConversation } from '../api/chatApi';

const Sidebar = ({ onSelectChat, onNewChat, toggleSidebar }) => {
  const [conversations, setConversations] = useState([]);

  const loadConversations = async () => {
    const data = await fetchConversations();
    setConversations(data);
  };

  useEffect(() => {
    loadConversations();
    const interval = setInterval(() => {
      loadConversations();
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  const handleNewChat = async () => {
    await onNewChat();
    loadConversations();
  };

  const handleDeleteChat = async (chatId) => {
    await deleteConversation(chatId);
    loadConversations();
  };

  return (
    <div className="h-screen bg-gray-900 text-white p-4 flex flex-col space-y-6">
      <div className="flex justify-between items-center">
        <button
          className="px-3 py-2 bg-gray-800 rounded-lg hover:bg-gray-700 transition"
          onClick={toggleSidebar}
        >
          Hide History
        </button>
        <button
          className="px-3 py-2 bg-green-500 rounded-lg hover:bg-green-400 transition"
          onClick={handleNewChat}
        >
          + New Chat
        </button>
      </div>
      <div className="overflow-y-auto flex-grow">
        <ul className="space-y-2">
          {conversations.map((chat) => {
            const firstPrompt =
              chat.history && chat.history[0] ? chat.history[0].prompt : 'Untitled Chat';

            return (
              <li
                key={chat._id}
                onClick={() => onSelectChat(chat._id)}
                className="flex justify-between items-center px-3 py-2 bg-gray-800 rounded-lg hover:bg-gray-700 transition cursor-pointer"
              >
                <span className="truncate">{firstPrompt}</span>
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    handleDeleteChat(chat._id);
                  }}
                  className="text-red-400 hover:text-red-300 transition"
                >
                  Delete
                </button>
              </li>
            );
          })}
        </ul>
      </div>
    </div>
  );
};

export default Sidebar;
