// export default Sidebar;
import React, { useEffect, useState } from 'react';
import { fetchConversations, deleteConversation } from '../api/chatApi';

const Sidebar = ({ onSelectChat, onNewChat, toggleSidebar }) => {
  const [conversations, setConversations] = useState([]);

  const loadConversations = async () => {
    const data = await fetchConversations();
    setConversations(data);
  };

  useEffect(() => {
    loadConversations();

    const interval = setInterval(() => {
      loadConversations();
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  const handleNewChat = async () => {
    await onNewChat();
    loadConversations();
  };

  const handleDeleteChat = async (chatId) => {
    await deleteConversation(chatId);
    loadConversations();
  };

  return (
    <div className="p-4 bg-gray-100 border-r border-gray-300 h-screen space-y-4">
      <button
        className="px-4 py-2 text-sm text-white bg-red-500 rounded-md hover:bg-red-600"
        onClick={toggleSidebar}
      >
        Hide Chat History
      </button>
      <button
        onClick={handleNewChat}
        className="px-4 py-2 text-sm text-white bg-green-500 rounded-md hover:bg-green-600"
      >
        New Chat
      </button>
      <ul className="space-y-4">
        {conversations.map((chat) => {
          const firstPrompt = chat.history && chat.history[0] ? chat.history[0].prompt : 'Untitled Chat';

          return (
            <li
              key={chat._id}
              onClick={() => onSelectChat(chat._id)}
              className="flex justify-between items-center p-2 bg-white border border-gray-300 rounded-md shadow hover:bg-gray-200 cursor-pointer"
            >
              <span className="text-gray-800">{firstPrompt}</span>
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  handleDeleteChat(chat._id);
                }}
                className="ml-2 px-2 py-1 text-sm text-red-500 underline hover:text-red-600"
              >
                Delete
              </button>
            </li>
          );
        })}
      </ul>
    </div>
  );
};

export default Sidebar;
