// export default ChatWindow;
import React, { useState, useEffect } from 'react';
import { fetchConversation, appendToConversation } from '../api/chatApi';

const ChatWindow = ({ chatId, isSidebarVisible }) => {
  const [chat, setChat] = useState(null);
  const [prompt, setPrompt] = useState('');
  const [file, setFile] = useState(null);
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    const loadChat = async () => {
      if (chatId) {
        const data = await fetchConversation(chatId);
        setChat(data);
      }
    };
    loadChat();
  }, [chatId]);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('prompt', prompt);
    if (file) {
      formData.append('file', file);
    }

    // User message
    const userMessage = {
      type: 'user',
      text: prompt,
    };

    // Bot's dummy response
    const botResponse = {
      type: 'bot',
      text: 'This is a dummy response', // Here you can replace this with actual bot response logic
    };

    // Add both the user and bot messages
    setMessages((prevMessages) => [...prevMessages, userMessage, botResponse]);

    // Reset the input fields
    setPrompt('');
    setFile(null);

    // Assuming you append the conversation to the backend here
    const updatedChat = await appendToConversation(chatId, prompt, botResponse.text, formData);
    setChat(updatedChat);
  };

  const firstPrompt = chat?.history && chat.history[0] ? chat.history[0].prompt : 'Untitled Chat';

  return (
    <div
      className={`flex flex-col h-screen bg-gray-50 transition-all duration-300 ${
        isSidebarVisible ? 'ml-22' : 'ml-0'
      }`}
    >
      {/* Header */}
      <header className="p-4 bg-gray-900 text-white shadow-md sticky top-0 z-10">
        <h1 className="text-xl font-semibold">{firstPrompt || 'Chat'}</h1>
      </header>

      {/* Chat History */}
      <div className="flex-grow overflow-y-auto p-6 bg-gray-700">
        {messages.map((message, index) => (
          <div
            key={index}
            className={`p-4 m-4 rounded-lg shadow-md border-spacing-1 ${
              message.type === 'bot'
                ? 'bg-blue-100 text-gray-900 self-center' // Bot message: center aligned
                : 'bg-gray-200 text-gray-900 self-end' // User message: right aligned
            }`}
          >
            <div className="font-medium">{message.type === 'user' ? 'You' : 'Bot'}</div>
            <div className="mt-2 text-sm">{message.text}</div>
          </div>
        ))}
      </div>

      {/* Footer */}
      <footer className="p-4 bg-gray-900">
        <form onSubmit={handleSubmit} className="flex items-center space-x-2">
          {/* Prompt Input */}
          <input
            type="text"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            placeholder="Type your prompt"
            className="flex-grow px-4 py-3 rounded-full bg-gray-800 text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-blue-500 shadow-md"
            required
          />
          {/* File Input */}
          <input
            type="file"
            onChange={handleFileChange}
            className="hidden"
            id="file-upload"
          />
          <label
            htmlFor="file-upload"
            className="flex items-center justify-center px-4 py-3 bg-gray-800 text-white rounded-full hover:bg-gray-700 cursor-pointer shadow-md"
          >
            ðŸ“Ž
          </label>
          {/* Submit Button */}
          <button
            type="submit"
            className="px-4 py-3 bg-blue-600 text-white rounded-full hover:bg-blue-500 shadow-md"
          >
            Send
          </button>
        </form>
      </footer>
    </div>
  );
};

export default ChatWindow;
