import React, { useState } from 'react';
import Sidebar from './components/Sidebar';
import ChatWindow from './components/ChatWindow';
import { createConversation } from './api/chatApi';

const App = () => {
  const [currentChatId, setCurrentChatId] = useState(null);
  const [sidebarVisible, setSidebarVisible] = useState(true);

  const handleNewChat = async () => {
    const newChat = await createConversation(`Chat ${Date.now()}`);
    setCurrentChatId(newChat._id); // Assuming the new chat object has an _id field
  };

  return (
    <div className="app flex w-full">
      {/* Sidebar */}
      <div
        className={`transition-all duration-300 ${sidebarVisible ? 'w-72' : 'w-16'}`}
      >
        <Sidebar
          onSelectChat={setCurrentChatId}
          onNewChat={handleNewChat}
          toggleSidebar={() => setSidebarVisible(!sidebarVisible)}
        />
      </div>

      {/* Chat Window */}
      <div className={`flex-grow transition-all duration-300 ${sidebarVisible ? 'ml-10' : 'ml-4'}`}>
        <ChatWindow chatId={currentChatId} isSidebarVisible={sidebarVisible}/>
      </div>

      {/* Button to Show Sidebar when Hidden */}
      {!sidebarVisible && (
        <button
          className="toggle-sidebar-btn fixed bottom-5 left-5 px-4 py-2 bg-gray-800 text-white rounded-full"
          onClick={() => setSidebarVisible(true)}
        >
          Show Sidebar
        </button>
      )}
    </div>
  );
};

export default App;
