// export default ChatWindow;
import React, { useState, useEffect } from 'react';
import { fetchConversation, appendToConversation } from '../api/chatApi';

const ChatWindow = ({ chatId }) => {
  const [chat, setChat] = useState(null);
  const [prompt, setPrompt] = useState('');
  const [file, setFile] = useState(null);

  useEffect(() => {
    const loadChat = async () => {
      if (chatId) {
        const data = await fetchConversation(chatId);
        setChat(data);
      }
    };
    loadChat();
  }, [chatId]);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('prompt', prompt);

    if (file) {
      formData.append('file', file);
    }

    const dummyResponse = 'This is a dummy response';

    const updatedChat = await appendToConversation(chatId, prompt, dummyResponse, formData);
    setPrompt('');
    setFile(null);
    setChat(updatedChat);
  };

  const firstPrompt = chat?.history && chat.history[0] ? chat.history[0].prompt : 'Untitled Chat';

  return (
    <div className="p-4 bg-white border border-gray-300 shadow-md rounded-md">
      {chatId && (
        <h3 className="text-lg font-bold text-gray-800 mb-4">
          Chat Title: {firstPrompt}
        </h3>
      )}

      <ul className="space-y-4">
        {chat?.history.map((entry, index) => (
          <li key={index} className="p-2 border border-gray-200 rounded-md">
            <div className="font-medium text-gray-800">{entry.prompt}</div>
            <div className="text-gray-600">{entry.response}</div>
          </li>
        ))}
      </ul>

      <form className="mt-4 space-y-4" onSubmit={handleSubmit}>
        <input
          type="text"
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder="Type your prompt"
          className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          required
        />
        <input
          type="file"
          onChange={handleFileChange}
          className="block w-full px-3 py-2 text-sm border border-gray-300 rounded-md"
        />
        <button
          type="submit"
          className="px-4 py-2 text-white bg-blue-500 rounded-md hover:bg-blue-600"
        >
          Send
        </button>
      </form>
    </div>
  );
};

export default ChatWindow;
